//..............................................................................
const {fs, path, util, pathConfiguration} = cxq;
//..............................................................................

//..............................................................................
const
{
    getBaseDomain,
    getApiDomain,
    getPresenceDomain,
    setBaseDomain,
    setApiDomain,
    setPresenceDomain,
    loadJsonSync,
} = util;
//..............................................................................

//..............................................................................
const configurationFile = path.join(pathConfiguration, 'remote.config.json');
if (fs.existsSync(configurationFile))
{
    const configurationJson = loadJsonSync(configurationFile);
    if (configurationJson)
    {
        if (configurationJson.api     ) setApiDomain     (configurationJson.api);
        if (configurationJson.presence) setPresenceDomain(configurationJson.presence);
    }
}
else
{
    console.info();
    console.info('-----------------------------------------------------------');
    console.info('WARNING : No remote.config.json, using defaults');
    console.info(configurationFile);
    console.info('-----------------------------------------------------------');
    console.info('API Domain      :', getApiDomain     ());
    console.info('Presence Domain :', getPresenceDomain());
    console.info('Base Domain     :', getBaseDomain    ());
    console.info('-----------------------------------------------------------');
    console.info();
}
//..............................................................................

