//..............................................................................
const {util} = cxq;
//..............................................................................

//..............................................................................
const
{
    addProperties
} = util;
//..............................................................................

//..............................................................................
function getAppName()
{
    return window.location.pathname.split('/')[1];
}
//..............................................................................

//..............................................................................
function getAppPath(urlString)
{
    var result  = '/' + getAppName();
    if (urlString)
        result += '/' + urlString;

    return result;
}
//..............................................................................

addProperties(util,
{
    getAppName,
    getAppPath,
});
