//..............................................................................
const {util} = cxq;
//..............................................................................

//..............................................................................
const
{
    processList,
    addProperties
} = util;
//..............................................................................

//..............................................................................
// These are capabilities that nobody should mess with!!
//..............................................................................
function getSystemCapabilityIds()
{
    const list =
    [
        'mesh-machine-core'   ,
        'mesh-package-manager',
        'mesh-device-finder'  ,
        'mesh-hardware-debug' ,
        'mesh-source-manager' ,
    ];
    const result = {};
    processList(list, function(id)
    {
        result[id] = id;
    });
    return result;
}
//..............................................................................

//..............................................................................
addProperties(util,
{
    getSystemCapabilityIds
});
//..............................................................................

