//..............................................................................
const {util} = cxq;
//..............................................................................

//..............................................................................
const
{
    addProperties,
    round100,
} = util;
//..............................................................................

//..............................................................................
const absoluteZero = -273.15; //In celsius
const oneGravity   = 9.80664999999998; //meter/secondÂ²
//..............................................................................

//..............................................................................
function accelerationToDegrees(xRaw,yRaw,zRaw)
{
    function toDegree(n)
    {
        return round100(n*180/3.141592);
    }
    ////////////////////////////////////////////////////////////////////////////
    var acos   = Math.acos;
    var sqrt   = Math.sqrt;
    var square = function(n){return Math.pow(n,2);};
    var length = sqrt(xRaw*xRaw + yRaw*yRaw + zRaw*zRaw);

    var r = {};
    if (length > 0)
    {
        r.x = acos(xRaw / length);
        r.y = acos(yRaw / length);
        r.z = acos(zRaw / length);
    }
    else
    {
        r.x = 0;
        r.y = 0;
        r.z = 0;
    }

    r.x = toDegree(r.x);
    r.y = toDegree(r.y);
    r.z = toDegree(r.z);

    return r;
}
//..............................................................................

//..............................................................................
function celsiusToFahrenheit(c)
{
    return (c * 9 / 5) + 32;
}
//..............................................................................

//..............................................................................
function fahrenheitToCelsius(f)
{
    return (f - 32) * 5 / 9;
}
//..............................................................................

//..............................................................................
function celsiusToKelvin(c)
{
    return c - absoluteZero;
}
//..............................................................................

//..............................................................................
function kelvinToCelsius(k)
{
    return k + absoluteZero;
}
//..............................................................................

//..............................................................................
function fahrenheitToKelvin(f)
{
    return celsiusToKelvin(fahrenheitToCelsius(f));
}
//..............................................................................

//..............................................................................
function kelvinToFahrenheit(k)
{
    return celsiusToFahrenheit(kelvinToCelsius(k));
}
//..............................................................................

//..............................................................................
function gravityToMpss(g)
{
    return (g * oneGravity);
}
//..............................................................................

//..............................................................................
function mpssToGravity(m)
{
    return (m / oneGravity);
}
//..............................................................................

addProperties(util,
{
    absoluteZero,
    oneGravity,  
    accelerationToDegrees,
    gravityToMpss,
    mpssToGravity,
    celsiusToFahrenheit,
    fahrenheitToCelsius,
    celsiusToKelvin,
    kelvinToCelsius,
    fahrenheitToKelvin,
    kelvinToFahrenheit
});


