//..............................................................................
const {fs, path} = cxq;

const {util, meshDocGen} = cxq;
const {addProperties} = util;

const{nodeVersion, gitVersion} = meshDocGen

const pathDocTop     = cxq.pathRoot;

const pathDocSource    = path.join(pathDocTop   , 'doc'                       );
const pathDocCode      = path.join(pathDocTop   , 'package-source', 'mesh-doc');
const pathGit          = path.join(pathDocTop   , 'git-v'   + gitVersion  + '-win-x64', 'bin');
const pathNode         = path.join(pathDocTop   , 'nodejs.' + nodeVersion                  );
const pathModuleCsvDS  = path.join(pathDocSource, 'docs', 'module-csv', 'device-sheet'     );
const pathModuleCsvPCB = path.join(pathDocSource, 'docs', 'module-csv', 'device-sheet'     );
const pathModuleDefs   = path.join(pathDocSource, 'docs', 'module-defs'                    );

const pathsOutput =
{
    final        : path.join(pathDocCode, 'output-final'       ),
    intermediate : path.join(pathDocCode, 'output-intermediate'),
    analysis     : path.join(pathDocCode, 'output-analysis'    ),
    reports      : path.join(pathDocCode, 'output-reports'     ),
    temp         : path.join(pathDocCode, 'output-temp'        ),
};
//..............................................................................
addProperties(cxq.meshDocGen,
{
    pathDocTop,
    pathDocSource,
    pathDocCode,
    pathModuleCsvDS,
    pathModuleDefs,
    pathsOutput,
    pathGit,
    pathNode,
});

console.log('Paths Set!');